<?phpnamespace App\Util;class Util{    static public function getSlug($cadena, $separador = '-')    {        $slug = iconv('UTF-8', 'ASCII//TRANSLIT', $cadena);        $slug = preg_replace("/[^a-zA-Z0-9\/_|+ -]/", '', $slug);        $slug = strtolower(trim($slug, $separador));        $slug = preg_replace("/[\/_|+ -]+/", $separador, $slug);        return $slug;    }    static public function getMonthName($month)    {        $meses = array(        1 => 'Enero',        'Febrero',        'Marzo',        'Abril',        'Mayo',        'Junio',        'Julio',        'Agosto',        'Septiembre',        'Octubre',        'Noviembre',        'Diciembre'        );        if($month >= 1 && $month<=12){            return $meses[(int)$month];        }else{            return "ErrorMes";        }    }    static public function CantBitFilter($value)    {        if($value >= 0 && $value < 1024)            return $value;//devuelvo el valor en B        elseif ($value >= 1024 && $value <1048576)            return number_format($value/1024,2);//devuelvo el valor en KB        elseif($value >= 1048576 && $value < 1073741824)            return number_format($value/1048576,2);//devuelvo el valor en MB        elseif($value >= 1073741824)            return number_format($value/1073741824,2);//devuelvo el valor en GB        else            return 0;    }    static public function CantBitTransform($unidad)    {        switch ($unidad)        {            case "GB":                return 1073741824;            case "MB":                return 1048576;            case "KB":                return 1024;            case "B":            default:                return 1;        }    }    static public function UnidadBitFilter($value)    {        if($value >= 0 && $value < 1024)            return "B";//devuelvo el valor en B        elseif ($value >= 1024 && $value <1048576)            return "KB";//devuelvo el valor en KB        elseif($value >= 1048576 && $value < 1073741824)            return "MB";//devuelvo el valor en MB        elseif($value >= 1073741824)            return "GB";//devuelvo el valor en GB        else            return "";    }}